class Exception extends Error {
/**
 * Exception contructor object
 * @param {Enum}    severity    - Serveity level
 * @param {number}  statusCode  - Http status code
 * @param {string}  code        - Error code
 * @param {string}  message     - Error message
 * @param {Object}  ctx         - Error context
 * @param {Object}  details     - Error details which will be also response to client
 */
  constructor(severity, statusCode, code, message, ctx, details) {
    super(message);
    this.name = this.constructor.name;
    this.severity = severity;
    this.statusCode = statusCode;
    this.code = code;
    this.ctx = ctx;
    this.details = details;
    Error.captureStackTrace(this, this.constructor);
  }
}

module.exports = Exception;
